import React, {Component} from 'react'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import CardGroup from 'react-bootstrap/CardGroup';

class MyCard extends Component{
  render(){
    return (
      <div>
        <Card bg="light" style={{ radius: "0px", width: '24vw'}}>
          <Card.Img variant="top" src={require('./imgs/leaf_background.jpg')} />
          <Card.Body>
            <Card.Title>
              Test 1
            </Card.Title>
            <Card.Text>
              This is some test text.
            </Card.Text>
            <Button variant="dark">Go somewhere</Button>
          </Card.Body>
        </Card>
      </div>
    );
  }
}

class Projects extends Component {
  //pull in information from some file, which will be our database, to make
  //these cards
  //file will contain picture, title, description, and github link
  render(){

    var fs = require('fs');
    var obj = JSON.parse(fs.readFileSync('./my_project.json'));
    console.log(obj);

    var cards = []
    for(var i = 0; i < 20; i++){
      cards.push(<MyCard/>);
    }

    return (
      <div>
        <CardGroup>
          {cards}
        </CardGroup>
      </div>
    );
  }
}

export default Projects
